package Java;

public class ArithmaticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//int data type
		int a=15;
		int b=10;
		System.out.println(a+b);//15+10=25
		System.out.println(a-b);//15-10=5
	    System.out.println(a*b);//15*10=150
	    System.out.println(a/b);//15/10=1
	    System.out.println(a%b);//15%10=5


	}

}
