package Java;

public class UnaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=15;
		System.out.println(a++);//a=15
		System.out.println(a);//a=16
		System.out.println(++a);//a=17
		System.out.println(a);//a=17
		System.out.println(a--);//a=17
		System.out.println(a);//a=16
		System.out.println(--a);//a=15


	}

}
